select mc.movie_id, c.country_id, c.name from movie_country mc
                                 join countries c on mc.country_id=c.country_id
                                 where mc.movie_id in (:movieIds);

select mg.movie_id, g.genre_id, g.name from movie_genre mg
                               join genres g on mg.genre_id=g.genre_id
                               where mg.movie_id in (:movieIds);
