select mc.movie_id, c.id, c.name from movie_country mc
                                 join country c on mc.country_id=c.id
                                 where mc.movie_id in (:movieIds);

select mg.movie_id, g.id, g.name from movie_genre mg
                               join genre g on mg.genre_id=g.id
                               where mg.movie_id in (:movieIds);

select mr.movie_id, r.id, r.text, u.id as user_id, u.user_name
                                from movie_reviews mr join reviews r on mr.review_id=r.id
                                join users u on r.user_id=u.id where mr.movie_id = :movieId;

select mc.movie_id, c.id, c.name from movie_country mc
                                 join country c on mc.country_id=c.id
                                 where mc.movie_id = :movieId;

select mg.movie_id, g.id, g.name from movie_genre mg
                               join genre g on mg.genre_id=g.id
                               where mg.movie_id = :movieId;
